def find_word(board, word):
    # initialise a default dictionary of boolean values to record whether a cell has been visited before
    visited = defaultdict(bool)
    # find valid start positions
    for row in range(len(board)):
        for col in range(len(board)):
            if board[row][col] == word[0]:
                visited[(row, col)] = True
                if len(word) == 1:
                    return True
                # continues searching
                if search(board, row, col, 1, word, visited):
                    return True
                else:
                    visited = defaultdict(bool)
    return False


def search(board, row, col, currIndex, word, visited):
    if currIndex == len(word):
        return True
    # check all the 8 neighbours
    for neighbour in range(8):
        rd = row + dx[neighbour]
        cd = col + dy[neighbour]
        # if the neighbours are off the edge of the board, discard them
        if rd < 0 or rd >= len(board) or cd < 0 or cd >= len(board[0]):
            continue
        # if we haven't visited that square already, and it matches the letter we're looking for
        if not visited[(rd, cd)] and board[rd][cd] == word[currIndex]:
            visited_cache = visited
            visited[(rd, cd)] = True
            if search(board, rd, cd, currIndex + 1, word, visited):
                return True
            visited = visited_cache


